mapper.select:
SELECT COUNT(*)
FROM TUSERACCOUNT
WHERE USER_EMAIL = #{USEREMAIL};

mapper.insert:
INSERT INTO TUSERACCOUNT( USER_EMAIL    ,USER_PWD
                         ,USER_NAME     ,USER_PHONE
                         ,USER_REGDATE  ,USER_SOCIAL
                         ,USER_STATE    ,USER_SEQ)
VALUES(  #{userEmail}   ,#{userPwd}
        ,#{userName}    ,#{userPhone}
        ,SYSDATE()      ,#{userSocial}
        ,#{userState}   ,(SELECT MAX(USER_SEQ)+1 FROM TUSERACCOUNT T1));
